package leetcode.easy;

import org.junit.Assert;
import org.junit.Test;

//https://leetcode.com/problems/is-subsequence/
public class IsSubsequence {
    public boolean isSubsequence2(String s, String t) {

        int prevIndex = -1;

        for (char c : s.toCharArray()) {
            int index = prevIndex >= 0 ? t.indexOf(c, prevIndex + 1) : t.indexOf(c);

            if (index == -1) {
                return false;
            }

            if (index < prevIndex) {
                return false;
            }
            prevIndex = index;
        }

        return true;
    }

    public boolean isSubsequence(String s, String t) {
        int leftBound = s.length();
        int rightBound = t.length();
        int pLeft = 0;
        int pRight = 0;

        while (pLeft < leftBound && pRight < rightBound) {
            // move both pointers or just the right pointer
            if (s.charAt(pLeft) == t.charAt(pRight)) {
                pLeft += 1;
            }
            pRight += 1;
        }
        return pLeft == leftBound;
    }

    @Test
    public void test() {
        Assert.assertFalse(isSubsequence("bb", "ahbgdc"));
    }

    @Test
    public void test7() {
        Assert.assertFalse(isSubsequence("aaaaaa", "bbaaaa"));
    }

    @Test
    public void test6() {
        Assert.assertFalse(isSubsequence("acb", "ahbgdc"));
    }

    @Test
    public void test5() {
        Assert.assertFalse(isSubsequence("axc", "ahbgdc"));
    }

    @Test
    public void test4() {
        Assert.assertTrue(isSubsequence("ab", "baab"));
    }

    @Test
    public void test3() {
        Assert.assertTrue(isSubsequence("abc", "ahbgdc"));
    }

    @Test
    public void test2() {
        Assert.assertTrue(isSubsequence("leeeeetcode",
            ""));
    }
}
